import { useState, useEffect } from 'react';
import axios from 'axios';
import './Landing.css';
import cad from '../assets/cad.png';
import Slider from './Slider';
const Landing = () => {
    const [items, setItems] = useState([])

    useEffect(() => {
        const fetchItems = async () => {
          const result = await axios(`https://www.breakingbadapi.com/api/characters?name`)
          setItems(result.data)

        }

        fetchItems()
      }, [])


    return (
        <>
            <h1>Cards</h1>
           <span className='red'><h1>Against</h1></span> 
            <h1> Humanity</h1>
            <div className='card'>
                <div className='card-inner'>
                    <div className='card-front'>
                        <h1>Instead of coal Santa now gives bad children</h1>
                        <div id="bottom">
                            <img className='bottom-img' src={cad} alt='logo'/>
                            Cards Against Humanity
                        </div>
                    </div>
                    <div className='card-back'>
                        <h1>Multiple stab wounds</h1>
                        <button className="enter-button">ENTER</button>
                        <div id="bottom">
                            <img className='bottom-img' src={cad} alt='logo'/>
                            Cards Against Humanity
                        </div>
                    </div>
                </div>
            </div>
           <Slider items={items}/>
        </>
    )
}
export default Landing;
