import { motion } from "framer-motion";
import { useRef, useEffect, useState } from "react";
import axios from 'axios';
import './Slider.css';
import images from "./images";
const Slider = () => {
    const [width, setWidth] = useState(0);
    const slider = useRef();
    const [items, setItems] = useState([])

    useEffect(() => {
        const fetchItems = async () => {
            const result = await axios(`https://cards-against-humanity-api.herokuapp.com/`)
            setItems(result.data)
            console.log(result.data)
        }
        fetchItems()
    setWidth(slider.current.scrollWidth - slider.current.offsetWidth)

    }, [])
    return (
        <>

        <div className="container">
        <h2>Question</h2>
        <h2> Card Options</h2>
            <motion.div ref={slider} className="slider" whileTap={{cursor:"grabbing"}}>
                <motion.div  drag="x"  dragConstraints={{right:0, left: -width}} className="inner-slider">
                {images.map(image => {
                    return(
                       <motion.div className="item"> 
                           <img src={image} alt="img"/>
                           </motion.div> 
                    );
                })}
                </motion.div>

            </motion.div>

        </div>
        </>
    );
}
export default Slider;
